public class WorkBenchCommand implements CommandExecutor {
    @Override
    public boolean onCommand(CommandSender sender, Command cmd, String label, String[] args) {
        Player p = (Player) sender;
        p.openWorkbench(p.getLocation(), true);
        return false;
    }
}
